document.addEventListener("DOMContentLoaded", function () {
    var swiper = new Swiper(".owl-service-item", {
      slidesPerView: 3, // Menampilkan 3 item sekaligus
      spaceBetween: 20, // Jarak antar item
      loop: true,
      navigation: {
        nextEl: ".swiper-button-next",
        prevEl: ".swiper-button-prev",
      },
      pagination: {
        el: ".swiper-pagination",
        clickable: true,
      },
      breakpoints: {
        1024: { slidesPerView: 3 },
        768: { slidesPerView: 2 },
        480: { slidesPerView: 1 },
      },
    });
  });
  